mapper.update:
        UPDATE pej_user
		SET user_num=#{userNum}, user_name=#{userName}, user_pass=#{userPass}
		WHERE user_id=#{userId}

mapper.select:
SELECT user_num, user_name, user_id, user_age
FROM pej_user
WHERE user_num = #{userNum}

select * 
from pej_user
where user_id = #{userId} and user_pass = #{userPass}
