mapper.update:
UPDATE pej_board
SET board_tit= #{boardTit}, board_con= #{boardCon}, board_up_date= now()
WHERE board_num= #{boardNum}

mapper.select:
SELECT board_num as boardNum, board_tit as boardTit , board_con as boardCon, board_rei_date as boardReiDate
FROM pej_board
WHERE board_num = #{boardNum}
